import React, { useState } from 'react'
import "./DevHome.css"
import ContentleftPanel from './ContentleftPanel'
import ContentBlogs from './ContentBlogs'
import { Link } from 'react-router-dom'
import { FaUser } from "react-icons/fa"
import IndividualDescription from './IndividualDescription'

const DevHome = (props) => {

    let contentWritings = props.contentWritings;

    const [search, setSearch] = useState('');
    const [showInfo, setShowInfo] = useState(false);
    const [details, setDetails] = useState({});

    const clickHandler = (content) => {
        setDetails({
            head: content.head,
            author: content.author
        })
        setShowInfo(true)
    }


    let contentDisplay = contentWritings.filter((content)=> {
        if(search == "") return content

        else if(content.head.toLowerCase().includes(search.toLowerCase()))
            return content

    }).map((content, key)=>(
        <ContentBlogs 
        key = {Math.random()}
        head = {content.head}
        author = {content.author}
        para = {content.para}
        onClick = {clickHandler(content)}
        />
    ))


    return (
    <>
         {!showInfo && <div className='devHome_body'>
            <div className='devHome_nav'>
                <Link to="/">
                <nav className='devHome_icon'>
                    <nav className='codeBlogs'>CodeBlogs</nav>
                    <nav className='rohan'>Blogging website for Devs!</nav>
                </nav>
                </Link>
                <nav className='dev_search'>
                    <input type='search' onChange={(e)=>(setSearch(e.target.value))} value={search} placeholder='Search...' className='devHome_search' />

                </nav>
                <nav className='devHome_profile'>
                <FaUser className='fauser'/>
                    <nav className='profile_desc'>
                        <nav className='profile_name'>Rohan</nav>
                        <nav className='profile_email'>abcsample@gmail.com</nav>
                    </nav>
                </nav>
            </div>
            <div className='devHome_content'>
                <ContentleftPanel />
                <div className='contentBlogs_flexBody'>
                    {contentDisplay}
                    {contentDisplay.length == 0 && <div className='noContent'>No Blogs Found:(</div>}
                </div>
            </div>
        </div>}
        {showInfo && <IndividualDescription

        />}
    </>
    )
}

export default DevHome
