import React from 'react'
import "./IndividualDescription.css"
import { MdOutlineArrowBackIos } from "react-icons/md"
import { Link, useLocation } from 'react-router-dom'
import Aos from "aos";
import "aos/dist/aos.css";

const IndividualDescription = () => {

    useEffect(function () {
        Aos.init();
      }, []);

    const location = useLocation();


    return (

            <div className='indi_body'>
                <Link to='../home'>
                    <MdOutlineArrowBackIos className='backIcon' />
                </Link>
                <div className='desc_heading'>{location.state.head}</div>
                <div className='desc_author'>{location.state.author}</div>
                <div className='desc_para'>
                    <p>
                    {location.state.para}
                    </p>
                </div>
            </div>

    )
}

export default IndividualDescription
